 */
public class AccessJava {

    public void ejecutar_ejemplo(){
        Rs r=new Rs();
        r.realizar_prueba();
    }

    public class Rs{
        /**
         * Para inicializar la coneccion.
         */
        private Conectar_a_Access mcn[]=new Conectar_a_Access[3];

        /**
         * Constructor por defecto.
         */
        public Rs(){
//            cn.conectar();
        }
        /**
         * Este constructor permite cargar la consulta sql al inicializarse.
         * @param nueva_consulta_sql Consulta sql al estilo SELECT [campo1][campo2] FROM [tabla1];
         */
        public Rs(String nueva_consulta_sql, int id_de_la_columna){
            mcn[id_de_la_columna]=new Conectar_a_Access();
            mcn[id_de_la_columna].conectar();
            try {
                this.setConsulta(nueva_consulta_sql, id_de_la_columna);
            } catch (SQLException ex) {
                Logger.getLogger(AccessJava.class.getName()).log(Level.SEVERE, null, ex);
            }
        }

        /**
         * Variable de objeto para mantener los datos de la consulta.
         */
        private ResultSet mrs_consulta[]=new ResultSet[3];
        /**
         * Permite realizar consultas.
         * @param sql Codigo sql para Access
         * @throws SQLException 
         */
        public void setConsulta(String sql, int id_de_la_columna) throws SQLException{
            mcn[id_de_la_columna]=new Conectar_a_Access();
            mcn[id_de_la_columna].conectar();
//            mrs_consulta[id_de_la_columna]=new ResultSet();
            this.mrs_consulta[id_de_la_columna]=this.mcn[id_de_la_columna].ejecutar_consulta(sql);
        }
        /**
         * Recupera la consulta realizada con sus datos.
         * @return Retorna un objeto ResultSet cargado con datos.
         */
        public ResultSet getConsulta(int id_de_la_columna){
            return this.mrs_consulta[id_de_la_columna];
        }

        /**
         * Las consultas solo se conectan a un campo a la vez.
         * Al getString del Recordset puede pasarsele un numero o el nombre de la columna como parametro.
         */
        public void realizar_prueba(){
//            INSERT INTO Tabla1 ( Nombre )VALUES (hola);
            try {
                ResultSet mCampos[]=new ResultSet[3];

                //No se pueden realizar consultas de insercion.
//                this.setConsulta("INSERT INTO Tabla1 ( Nombre )VALUES (hola);", 1);
                this.setConsulta("SELECT [id] FROM [tabla1]", 0);
                this.setConsulta("SELECT [Nombre] FROM [tabla1]", 1);
                this.setConsulta("SELECT [Edad] FROM [tabla1]", 2);

                while (this.getConsulta(0).next()==true) {
                    //Cada numero significa una columana.
                    System.out.println("id=" + getConsulta(0).getInt(1));
                    getConsulta(1).next();
                    System.out.println("Nombre=" + getConsulta(1).getString(1));
                    getConsulta(2).next();
                    System.out.println("Edad=" + getConsulta(2).getString(1));
                }
            } catch (SQLException ex) {
                Logger.getLogger(AccessJava.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }

    /**
     * Clase para conectarse a una base de datos de Access.
     * Se le debe dar la direccion completa de la base de datos.
     * Esta direccion debe ser modificada cuando se descargue y comprima.
     */
    public class Conectar_a_Access{

        public void cerrar(){
            try {
                conexion.close();
            } catch (SQLException ex) {
                Logger.getLogger(AccessJava.class.getName()).log(Level.SEVERE, null, ex);
            }
        }

        /**
         * Una instancia u objeto de Connection.
         * Sirve para establecer la conexion con la base de datos.
         */
        private Connection conexion;
        /**
         * Variable, objeto, instancia de Statement, sirve para establecer el tipo de conexion, solo lectura, solo escritura, ambos si es que existe...
         */
        private Statement sentencia;
        /**
         * Nombre del controlador.
         */
        private final String UCANACCESS="jdbc:ucanaccess://";

        /**
         * Es el controlador de conecciones.
         */
        private final String controlador="sun.jdbc.odbc.JdbcOdbcDriver";
        /**
         * Direccion con el nombre y la extencion de la base de datos.
         * Debe modificarse esta direccion al descomprimir la carpeta.
         * La base de datos se encuentra en AccessJava la cual es la carpeta raíz del proyecto.
         */
        private final String BASE_DE_DATOS="C:\\Biblioteca de Rafael\\Clases Java hechas por mi\\AccessJava\\Base.mdb";
        private final String USER="";
        private final String CLAVE="";

        /**
         * Ejecuta una consulta sql.
         * @param sql Cadena de texto que representa una consulta sql para Access, recordar que el sql varia de un motor de bases de datos a otro.
         * @return Retorna un objeto ResultSet ya inicalizado y cargado con informacion obtenida por medio de la consulta.
         * @throws SQLException 
         */
        public ResultSet ejecutar_consulta( String sql) throws SQLException{
            ResultSet rs;
            rs = this.sentencia.executeQuery(sql);
            return rs;
        }

        /**
         * Constructor bacio.
         */
        public Conectar_a_Access(){

        }

        /**
         * Inicializa la variable sentencia.
         * Establece el comportamiento de la conexion.
         * 
         * Debe ser llamado en la funcion: conectar.
         */
        private void cargar_sentencia(){
            try { 
            this.sentencia=this.conexion.createStatement( 
            ResultSet.TYPE_SCROLL_SENSITIVE, 
                    ResultSet.CONCUR_READ_ONLY); 
        } 
        catch (SQLException e) { 
            JOptionPane.showMessageDialog(null,"Error al inicializar la variable sentencia: "+e);
        }
        }

        /**
         * Funcion principal, debe ejecutarse de primero.
         * @return Retorna verdadero si la coneccion fue exitosa.
         */
        public boolean conectar(){
            try {
                this.conexion=DriverManager.getConnection(this.UCANACCESS+this.BASE_DE_DATOS,this.USER,this.CLAVE);
                cargar_sentencia();
                return true;
            } catch (SQLException ex) {
                Logger.getLogger(AccessJava.class.getName()).log(Level.SEVERE, null, ex);
            }
            return false;
        }

    }

    /**
     * @param args Vector para la entrada de linea de comandos.
     */
    public static void main(String[] args) {
        AccessJava cnA=new AccessJava();
        cnA.ejecutar_ejemplo();
    }
}


